import timefrom io_mesh_dm.dm_parse import parse_mainfrom io_mesh_dm.dm_import import crete_meshstart_time = time.time()dm_file_path = 'c:\\'dm_file_name = 'dm_lightning-01-glow'dm_file_ext = '.dm'print('read file {}...'.format(dm_file_path))dm_file = open(dm_file_path + dm_file_name + dm_file_ext, 'rb')dm_data = dm_file.read()dm_file.close()print('size {0} = {1} bytes'.format(dm_file_path, len(dm_data)))vertices, uvs, triangles, texture_name = parse_main(dm_data)crete_mesh(dm_file_name, vertices, uvs, triangles, texture_name)finish_time = time.time()print('total time: {}s'.format(finish_time - start_time))