def read_file():    import time    from . import dm_parse    from . import dm_import    from . import dm_prop        start_time = time.time()    dm_file_ext = '.dm'    dm_file = open(dm_prop.dm_file_path + dm_prop.dm_file_name + dm_file_ext, 'rb')    dm_data = dm_file.read()    dm_file.close()    vertices, uvs, triangles, texture_name = dm_parse.parse_main(dm_data)    dm_import.crete_mesh(dm_prop.dm_file_name, vertices, uvs, triangles, texture_name)    finish_time = time.time()    print('Import {}.dm complete ({}s)'.format(dm_prop.dm_file_name, finish_time - start_time))read_file()