def calc_file_size(s):	"""	вычисляет размер переданных данных файла	использование: calc_file_size(s)	где s = (open(path, 'r')).read()	"""	file_size = str(len(s))	if len(file_size) < 3:		print('file size {0} bytes'.format(file_size))	elif len(file_size) == 3:		print('file size 0.{0} Kb'.format(file_size))	elif 3 < len(file_size) < 7:		print('file size {0}.{1} Kb'.format(file_size[:-3], file_size[-3:]))	elif len(file_size) >= 7:		print('file size {0}.{1} Mb'.format(file_size[:-6], file_size[-6:-3]))